glsl3.vert:
precision highp float;
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
};
in vec3 a_position;
in vec2 a_texCoord;
in vec4 a_color;
out vec4 color;
out vec2 uv0;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  uv0 = a_texCoord;
  color = a_color;
  return pos;
}
void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
vec4 CCSampleTexture(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
    return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
    return texture(tex, uv);
#endif
}
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
};
in vec4 color;
in vec2 uv0;
uniform sampler2D cc_spriteTexture;
layout(std140) uniform Constant {
  vec4 borderColor;
  float precent;
  float borderWidth;
};
vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);
  vec4 h = vec4(.0, .0, .0, 1);
  float pi = 3.1415926;
  #if USE_TEXTURE
    o *= CCSampleTexture(cc_spriteTexture, uv0);
  #endif
  vec2 center = vec2(.5, .5);
  float radius = .5;
  float dy = uv0.y - center.y;
  float dx = uv0.x - center.x;
  float dis = sqrt(pow(uv0.x - center.x, 2.) + pow(uv0.y - center.y, 2.));
  if(dis > radius) {
    o.a = .0;
  }
  if(precent < 100.) {
    float tan0 = atan(dy, dx);
    float ang =(tan0 + pi*.5)/pi * 180.;
    o *= color;
    float te = mod(precent, 100.) * 3.6;
    float gray  = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
    if(mod(ang, 360.) > te && mod(ang, 360.) <= 360.) {
      o.xyz = mix(o.xyz, h.xyz, .5);
    }
  }
  if( dis < 1. && dis > .5*(1. - borderWidth)) {
    o.xyz = mix(o.xyz, h.xyz, .5);
  }
  return o;
}
out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

glsl1.vert:
precision highp float;
uniform highp mat4 cc_matViewProj;
attribute vec3 a_position;
attribute vec2 a_texCoord;
attribute vec4 a_color;
varying vec4 color;
varying vec2 uv0;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  uv0 = a_texCoord;
  color = a_color;
  return pos;
}
void main() { gl_Position = vert(); }

glsl1.frag:
precision highp float;
vec4 CCSampleTexture(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
    return vec4(texture2D(tex, uv).rgb, texture2D(tex, uv + vec2(0.0, 0.5)).r);
#else
    return texture2D(tex, uv);
#endif
}
varying vec4 color;
varying vec2 uv0;
uniform sampler2D cc_spriteTexture;
uniform float precent;
uniform float borderWidth;
vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);
  vec4 h = vec4(.0, .0, .0, 1);
  float pi = 3.1415926;
  #if USE_TEXTURE
    o *= CCSampleTexture(cc_spriteTexture, uv0);
  #endif
  vec2 center = vec2(.5, .5);
  float radius = .5;
  float dy = uv0.y - center.y;
  float dx = uv0.x - center.x;
  float dis = sqrt(pow(uv0.x - center.x, 2.) + pow(uv0.y - center.y, 2.));
  if(dis > radius) {
    o.a = .0;
  }
  if(precent < 100.) {
    float tan0 = atan(dy, dx);
    float ang =(tan0 + pi*.5)/pi * 180.;
    o *= color;
    float te = mod(precent, 100.) * 3.6;
    float gray  = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
    if(mod(ang, 360.) > te && mod(ang, 360.) <= 360.) {
      o.xyz = mix(o.xyz, h.xyz, .5);
    }
  }
  if( dis < 1. && dis > .5*(1. - borderWidth)) {
    o.xyz = mix(o.xyz, h.xyz, .5);
  }
  return o;
}
void main() { gl_FragColor = frag(); }
